/**
 * Warning class - parse the warning and creates icon for that warning
 * 
 */
var CATEGORY_WAITING_FOR_OPENING_AT_WAY_POINT = 16;

Warning = function (warningJson, duration) {
	
	this.warningMsg = warningJson.message;
	this.warningCode = warningJson.code;
	if (duration != undefined) this.warningConditionDuration = Math.floor(duration / 1000);
	else this.warningConditionDuration = 0;
	this.icon = populateIconField(this.warningMsg, this.warningConditionDuration, warningJson);
	var scope = this;

 return this;
}

//--- Returns icon
Warning.prototype.getIcon = function() {
	
    return this.icon;
}

//--- Creates icon for the Warning

function populateIconField(warningMsg, warningConditionDuration, warningJson){
	
	var displayMsg = createMessageForDisplay(warningMsg, warningConditionDuration, warningJson);
    var displayMsgHeading = displayMsg.heading !== undefined ? displayMsg.heading : 'Warning';
    var restTime = displayMsg.restTime !== undefined ? displayMsg.restTime : warningMsg;
    var warningType = displayMsg.type !== undefined ? displayMsg.type : 'Default';
    var icon = createWarningIconMarker(displayMsgHeading, restTime, warningType);
    return icon;
	
}

//--- Parse the warning message and extracts warning type and message for displaying

function createMessageForDisplay(message, duration, warningJson){
	var displayMsg = new Object();
	if (message){
		if (message.toLowerCase().indexOf("taking the short driver rest time") !== -1 ){ //short rest time
			displayMsg.type = 'DriverRestTime';
			displayMsg.heading = 'Short Rest Time';
			if (message.indexOf("on learned stop sequence #") !== -1) 
				displayMsg.restTime = message.substring(message.indexOf('on learned stop'),message.lastIndexOf('sec')+3);
			else
				displayMsg.restTime = "after " + humanReadabletime(message.substring(message.indexOf('after') + 5 ,message.indexOf('sec'))) + " for " + humanReadabletime(message.substring(message.indexOf('for') + 3 ,message.indexOf('sec at routeLinkSeqNum'))) ;
		}else if(message.toLowerCase().indexOf("taking the long driver rest time") !== -1 ){ //long rest time
			displayMsg.type = 'DriverRestTime';
			displayMsg.heading = 'Long Rest Time';
			if (message.toLowerCase().indexOf("while waiting on a time condition") !== -1 )
				displayMsg.restTime = message.substring(message.indexOf('after'),message.lastIndexOf('at')) + ", " +  message.substring(message.indexOf('while'), message.length);
			else if (message.indexOf("on learned stop sequence #") !== -1) 
				displayMsg.restTime = message.substring(message.indexOf('on learned stop'),message.lastIndexOf('sec')+3);
			else 
           displayMsg.restTime = "after " + humanReadabletime(message.substring(message.indexOf('after') + 5 ,message.indexOf('sec'))) + " for " + humanReadabletime(message.substring(message.indexOf('for') + 3 ,message.lastIndexOf('sec'))) ;
		}else if(message.toLowerCase().indexOf("condition applies for next") !== -1){
			displayMsg.type = 'WaitingDueToCondition';
			displayMsg.heading = 'Waiting On Condition';
			displayMsg.restTime = "Waiting for " + humanReadabletime(warningJson.duration) + message.substring(message.indexOf('sec')+3,message.lastIndexOf('at') - 1);
		}else if (message.indexOf('at way point') !== -1 && message.indexOf('until it opens') !== -1){
			displayMsg.type = 'OpeningTime';
			displayMsg.heading = 'Opening Time Constraint';
			displayMsg.restTime = "Waiting for " + humanReadabletime(message.substring(message.indexOf(' '), message.indexOf('seconds') - 1)) + message.substring(message.indexOf('seconds')+7, message.length-1);
		}else if(message.toLowerCase().indexOf("waiting") !== -1){
            displayMsg.type = 'WaitingDueToCondition';
			displayMsg.heading = 'Waiting On Condition';
            if (message.toLowerCase().indexOf("due") !== -1){
            displayMsg.restTime = "Waiting for " + humanReadabletime(warningJson.duration) + message.substring(message.indexOf('sec')+3,message.lastIndexOf('routeLinkSeqNr')) + message.substring(message.lastIndexOf('due'), message.length-1) + " in " + warningJson.isoCountryCode;
             } else {
            displayMsg.restTime = "Waiting for " + humanReadabletime(message.substring(message.indexOf(' '),message.lastIndexOf('sec'))) + message.substring(message.lastIndexOf('sec')+3, message.length-1);
          }
		}else if(message.toLowerCase().indexOf("suspicious u-turn") !== -1){
			displayMsg.type = 'Default';
			displayMsg.heading = 'Suspicious u-turn';
			displayMsg.restTime = message.substring(0,message.lastIndexOf(' '));
		}else if(message.toLowerCase().indexOf("vehicle stopped") !== -1){
			displayMsg.type = 'StopDetected';
			displayMsg.heading = '';
			displayMsg.restTime = message.substring(0,message.lastIndexOf('until')) + "for " + humanReadabletime(duration);
		}else if(warningJson.code !== undefined && (warningJson.code == 1019 || warningJson.code == 1021)) {
            displayMsg.type = 'OutOfEnergy';
			displayMsg.heading = 'Out of Energy';
			displayMsg.restTime = message.replace("Route returned is not optimized for energy consumption. ", "");
        }
	}
	return displayMsg;
}


//--- Create marker with 2 text lines
var createWarningIconMarker = function (line1, line2, warningType, language) {
    var svgMarker = svgMarkerImage_Maneuver;
    var width = line2.length * 5 + 25;
    if (warningType){
        if (warningType === 'DriverRestTime'){
            svgMarker = line1 === 'Long Rest Time' ? svgMarkerImage_DriverLongRestTime : svgMarkerImage_DriverShortRestTime;
        }else if (warningType === 'WaitingDueToCondition'){
            svgMarker = svgMarkerImage_ConditionalWaiting;
        }else if (warningType === 'StopOver'){
            svgMarker = svgMarkerImage_StopOverDelay;
        }else if (warningType === 'OpeningTime'){
            svgMarker = svgMarkerImage_openingTime;
        }else if (warningType === 'StopDetected'){
            svgMarker = svgMarkerImage_StopDetection;
            width = line2.length * 5 + 40;
        }else if (warningType === 'OutOfEnergy'){
            svgMarker = svgMarkerImage_OutOfEnergy;
            width = line2.length * 6;
        }else if (warningType === 'guidance'){
            if(language == 'zh-cn' || language == 'zh-tw' || language == 'zh-hk')
                width += 35;
            else if (language == 'ja-jp')
                width += 150;
            else
                width += 15;        
        }
    }
    svgMarker = svgMarker.replace(/__line1__/g, line1);
    svgMarker = svgMarker.replace(/__line2__/g, line2);
    svgMarker = svgMarker.replace(/__width__/g, width);
    svgMarker = svgMarker.replace(/__widthAll__/g, width * 1.5);
    svgMarker = svgMarker.replace(/font-size=\"8\"/g, 10);
   //return new H.map.Icon(svgMarker, { anchor: new H.math.Point(24, 57)	});
    return new H.map.Icon(svgMarker, { anchor: new H.math.Point(18, 48)	});
};


var humanReadabletime = function(timeSeconds) {
    if (timeSeconds < 60)
        return timeSeconds + "s ";
    if (timeSeconds < 3600)
        return Math.floor((timeSeconds / 60)) + "min " + timeSeconds % 60 + 's';
    else
        return Math.floor((timeSeconds / 3600)) + "h " + Math.floor((timeSeconds / 60) % 60) + "min " + timeSeconds % 60 + 's';
};



